import { setTitle } from 'meta';

import {
  hideChunkGraph,
  showChunkGraph,
} from 'graphs/chunks';

export default async (render, options) => {
  setTitle('Chunks');

  const template = await import('./view.jade');
  render(template.default);

  showChunkGraph();

  options.registerMethod('componentWillUnmount', () => hideChunkGraph());
};
